create procedure administrator.sp_payments(@FromDate date,@ToDate date)
as
select a.AccountNumber,vNum=a.VendorAccountNumber,
  cNum=a.CustAccountNumber,FullName=STRING(f.first_name,' ',last_name),rate=(select p.Amount from
    Price as p where p.FromDate = @FromDate and p.ToDate = @ToDate),
  Balance=ISNULL(a.OpenBalance,0.0),SumQty=ISNULL((select SUM(c.quantity) from collections as c where
    c.txn_date between @FromDate and @ToDate and vNum = c.member_number),0.0),
  amtOwed=round((rate*sumQty),3),sharesDeduction=ISNULL((select st.AmountDeducted from ShareTransaction as st join
    MemberShare as ms on ms.code = st.ShareCode where st.FromDate = @FromDate and
    st.ToDate = @ToDate and st.MemberNumber = a.AccountNumber),0.0),
  stores=ISNULL((select SUM(amount) from CreditSales where BeginDate = @FromDate and EndDate = @ToDate and
    SalesClass = 'Stores' and MemberNumber = cNum),0.0),Vet_AI=ISNULL((select amount from CreditSales where
    BeginDate = @FromDate and EndDate = @ToDate and SalesClass = 'Vet Services:A.I' and
    MemberNumber = cNum),0.0),Vet_Clinical=ISNULL((select amount from CreditSales where BeginDate = @FromDate and
    EndDate = @ToDate and SalesClass = 'Vet Services:Clinical Services' and MemberNumber = cNum),0.0),
  TotDeductions=(Stores+Vet_AI+Vet_Clinical),NetPayable=(amtOwed-TotDeductions-Balance) into Payments
  from Accounts as a join farmer as f on a.AccountNumber = f.account_number
go
COMMENT TO PRESERVE FORMAT ON PROCEDURE "administrator"."sp_payments" IS 
{create PROCEDURE administrator."sp_payments"(IN @FromDate DATE, IN @ToDate DATE )
AS
	SELECT a.AccountNumber, a.VendorAccountNumber vNum, 
			a.CustAccountNumber cNum,STRING(f.first_name, ' ', last_name)FullName, ( SELECT p.Amount FROM
			Price p WHERE p.FromDate = @FromDate AND p.ToDate = @ToDate) rate,
			ISNULL(a.OpenBalance,0.00) Balance,ISNULL((SELECT SUM(c.quantity) FROM collections c
			WHERE c.txn_date BETWEEN @FromDate AND @ToDate AND vNum = c.member_number),0.00) SumQty, 
			round((rate * sumQty),3) amtOwed,ISNULL((SELECT st.AmountDeducted FROM ShareTransaction 
			st INNER JOIN MemberShare ms ON ms.code = st.ShareCode WHERE st.FromDate = @FromDate
			AND st.ToDate = @ToDate AND st.MemberNumber = a.AccountNumber ),0.0)sharesDeduction, 
			ISNULL((SELECT SUM(amount) FROM CreditSales WHERE BeginDate = @FromDate AND EndDate = @ToDate 
			AND SalesClass = 'Stores' AND MemberNumber = cNum),0.00) stores, ISNULL((SELECT amount FROM CreditSales 
			WHERE BeginDate = @FromDate AND EndDate =  @ToDate  AND SalesClass = 'Vet Services:A.I' 
			AND MemberNumber = cNum),0.00) Vet_AI, ISNULL((SELECT amount FROM CreditSales WHERE BeginDate = @FromDate
			AND EndDate = @ToDate  AND SalesClass = 'Vet Services:Clinical Services' AND MemberNumber = cNum),0.00) Vet_Clinical, 
			(Stores + Vet_AI + Vet_Clinical ) TotDeductions,( amtOwed -  TotDeductions  - Balance ) NetPayable 
			INTO Payments FROM Accounts a INNER JOIN farmer f ON a.AccountNumber = f.account_number
}
go
GRANT EXECUTE ON "administrator"."sp_payments" TO "administrator"
go

create procedure administrator.sp_memberMPR(@FromDate date,@ToDate date,@MemberNumber varchar(15))
as
select distinct mem_num=SUBSTRING(c.member_number,2),name=string(f.first_name,' ',f.last_name),txnDate=c.txn_date,
  morning_collections=ISNULL((select quantity from collections where txn_date = txnDate and
    sessions = 'AM' and member_number = @MemberNumber),0),
  afternoon_collections=ISNULL((select quantity from collections where txn_date = txnDate and sessions = 'PM' and
    member_number = @MemberNumber),0),qty_reject=ISNULL((select quantity from reject where
    reject_date = txnDate and
    member_number = @MemberNumber),0),total=(morning_collections+afternoon_collections),
  adjusted_total=(total-qty_reject) from
  collections as c,farmer as f where c.member_number = f.member_num and
  c.member_number = @MemberNumber and c.txn_date between @FromDate and @ToDate order by
  c.txn_date asc
