ISNULL((SELECT quantity FROM collections WHERE txn_date = txnDate AND 
sessions = 'AM' AND member_number = MemberNumber),0) AS morning_collections,
ISNULL((SELECT quantity FROM collections WHERE txn_date = txnDate AND sessions = 'PM' AND 
member_number = MemberNumber),0) AS afternoon_collections,ISNULL((SELECT quantity FROM reject
 WHERE reject_date = txnDate AND
member_number = MemberNumber),0) AS qty_reject, (morning_collections + afternoon_collections) AS total,
(total- qty_reject) AS adjusted_total 
FROM collections c, farmer f WHERE c.member_number = f.member_num 
AND c.member_number = MemberNumber AND c.txn_date BETWEEN FromDate AND ToDate 
ORDER BY c.txn_date ASC
}
