create procedure administrator.sp_removeDuplicateMilkTxns()
begin
  select member_number as MemNum,txn_date as TxNDate,sessions into #dupsTbl from collections group by member_number,txn_date,
    sessions having count(member_number) > 1;
  delete top 1 from collections where member_number = dupsTbl.MemNum and dupsTbl.txn_date = TxNDate and
    sessions = dupsTbl.TxnSession
end
go
