{create PROCEDURE administrator."sp_removeDuplicateMilkTxns"( )

BEGIN
	SELECT member_number MemNum, txn_date TxNDate, sessions INTO #dupsTbl FROM collections GROUP BY member_number,txn_date, 
        sessions HAVING count(member_number) > 1;
DELETE TOP 1 FROM collections WHERE member_number = dupsTbl.MemNum AND dupsTbl.txn_date = TxNDate
         AND sessions = dupsTbl.TxnSession  
END
}
